package com.boilerplate.auth.config;

import lombok.extern.slf4j.Slf4j;
import org.apache.kafka.clients.producer.ProducerConfig;
import org.apache.kafka.common.serialization.StringSerializer;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.kafka.core.DefaultKafkaProducerFactory;
import org.springframework.kafka.core.KafkaTemplate;
import org.springframework.kafka.core.ProducerFactory;
import org.springframework.kafka.support.serializer.JsonSerializer;

import java.util.HashMap;
import java.util.Map;

/**
 * Kafka Producer Configuration cho Auth Service
 * 
 * Production-ready với:
 * - Retry mechanism
 * - Idempotence
 * - Compression
 * - Monitoring metrics
 */
@Configuration
@Slf4j
public class KafkaProducerConfig {

    @Value("${spring.kafka.bootstrap-servers}")
    private String bootstrapServers;

    @Value("${spring.kafka.producer.acks:all}")
    private String acks;

    @Value("${spring.kafka.producer.retries:3}")
    private Integer retries;

    @Value("${spring.kafka.producer.compression-type:snappy}")
    private String compressionType;

    @Value("${spring.kafka.producer.max-in-flight-requests-per-connection:5}")
    private Integer maxInFlightRequests;

    @Value("${spring.kafka.producer.enable-idempotence:true}")
    private Boolean enableIdempotence;

    @Value("${spring.kafka.producer.request-timeout-ms:30000}")
    private Integer requestTimeoutMs;

    @Value("${spring.kafka.producer.delivery-timeout-ms:120000}")
    private Integer deliveryTimeoutMs;

    /**
     * Cấu hình Producer Factory với các settings production-ready
     */
    @Bean
    public ProducerFactory<String, Object> producerFactory() {
        Map<String, Object> configProps = new HashMap<>();

        // Bootstrap servers
        configProps.put(ProducerConfig.BOOTSTRAP_SERVERS_CONFIG, bootstrapServers);

        // Serializers
        configProps.put(ProducerConfig.KEY_SERIALIZER_CLASS_CONFIG, StringSerializer.class);
        configProps.put(ProducerConfig.VALUE_SERIALIZER_CLASS_CONFIG, JsonSerializer.class);

        // Reliability settings
        configProps.put(ProducerConfig.ACKS_CONFIG, acks);
        configProps.put(ProducerConfig.RETRIES_CONFIG, retries);
        configProps.put(ProducerConfig.ENABLE_IDEMPOTENCE_CONFIG, enableIdempotence);

        // Performance & Compression
        configProps.put(ProducerConfig.COMPRESSION_TYPE_CONFIG, compressionType);
        configProps.put(ProducerConfig.LINGER_MS_CONFIG, 10); // Batch messages trong 10ms
        configProps.put(ProducerConfig.BATCH_SIZE_CONFIG, 32768); // 32KB batch size

        // Timeout settings
        configProps.put(ProducerConfig.MAX_IN_FLIGHT_REQUESTS_PER_CONNECTION, maxInFlightRequests);
        configProps.put(ProducerConfig.REQUEST_TIMEOUT_MS_CONFIG, requestTimeoutMs);
        configProps.put(ProducerConfig.DELIVERY_TIMEOUT_MS_CONFIG, deliveryTimeoutMs);

        // Metadata
        configProps.put(ProducerConfig.CLIENT_ID_CONFIG, "auth-service-producer");

        // JSON Serializer settings
        configProps.put(JsonSerializer.ADD_TYPE_INFO_HEADERS, false);
        configProps.put(JsonSerializer.TYPE_MAPPINGS, 
            "supportTicketEvent:com.boilerplate.auth.model.event.SupportTicketEvent");

        log.info("Kafka Producer initialized with bootstrap servers: {}", bootstrapServers);

        return new DefaultKafkaProducerFactory<>(configProps);
    }

    /**
     * KafkaTemplate bean để gửi messages
     */
    @Bean
    public KafkaTemplate<String, Object> kafkaTemplate() {
        KafkaTemplate<String, Object> template = new KafkaTemplate<>(producerFactory());

        // Thêm observer để log thành công/thất bại
        template.setObservationEnabled(true);

        log.info("KafkaTemplate bean created successfully");
        return template;
    }
}

package com.boilerplate.auth.service;

import com.boilerplate.auth.entity.User;
import com.boilerplate.auth.entity.Vehicle;
import com.boilerplate.auth.enums.SupportTicketType;
import com.boilerplate.auth.enums.TicketPriority;
import com.boilerplate.auth.exception.ResourceNotFoundException;
import com.boilerplate.auth.model.event.SupportTicketEvent;
import com.boilerplate.auth.repository.UserRepository;
import com.boilerplate.auth.repository.VehicleRepository;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.slf4j.MDC;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import java.time.Instant;
import java.util.List;
import java.util.UUID;

/**
 * Service demo cách sử dụng Support Ticket Event Publishing
 * 
 * Best practices:
 * - Validate input trước khi tạo event
 * - Use correlation ID cho distributed tracing
 * - Handle async result với CompletableFuture
 * - Log đầy đủ cho debugging
 * - Transactional consistency
 */
@Service
@RequiredArgsConstructor
@Slf4j
public class SupportTicketDemoService {

    private final UserRepository userRepository;
    private final VehicleRepository vehicleRepository;
    private final SupportTicketEventBuilder eventBuilder;
    private final SupportTicketEventPublisher eventPublisher;

    /**
     * Example 1: Tạo simple support ticket
     */
    public String createSimpleSupportTicket(
            String employeeId,
            String title,
            String description,
            SupportTicketType ticketType
    ) {

        // Generate correlation ID for tracing
        String correlationId = UUID.randomUUID().toString();
        MDC.put("correlationId", correlationId);

        try {
            log.info("Creating simple support ticket: employeeId={}, type={}", 
                employeeId, ticketType);

            // Load user
            User user = userRepository.findByEmployeeId(employeeId)
                .orElseThrow(() -> new ResourceNotFoundException(
                    "Không tìm thấy nhân viên với mã: " + employeeId));

            // Generate ticket ID
            String ticketId = generateTicketId();

            // Build event
            SupportTicketEvent event = eventBuilder.buildSimpleTicketCreatedEvent(
                ticketId,
                user.getEmployeeId(),
                user.getFullName(),
                user.getEmail(),
                ticketType,
                title,
                description
            );

            // Set correlation ID
            event.setCorrelationId(correlationId);

            // Publish event asynchronously
            eventPublisher.publishSupportTicketCreated(event)
                .whenComplete((result, ex) -> {
                    if (ex == null) {
                        log.info("✓ Support ticket event published: ticketId={}", ticketId);
                    } else {
                        log.error("✗ Failed to publish support ticket: ticketId={}", 
                            ticketId, ex);
                        // TODO: Alert, retry, or save to DLQ
                    }
                });

            log.info("Support ticket created successfully: ticketId={}", ticketId);
            return ticketId;

        } finally {
            MDC.remove("correlationId");
        }
    }

    /**
     * Example 2: Tạo detailed support ticket với full context
     */
    @Transactional
    public String createDetailedSupportTicket(
            String employeeId,
            String vehicleId,
            Long stationId,
            String stationName,
            String stationAddress,
            String batteryPackId,
            SupportTicketType ticketType,
            TicketPriority priority,
            String title,
            String description,
            Instant incidentTime,
            List<String> attachments
    ) {

        String correlationId = UUID.randomUUID().toString();
        MDC.put("correlationId", correlationId);

        try {
            log.info("Creating detailed support ticket: employeeId={}, vehicleId={}, type={}, priority={}", 
                employeeId, vehicleId, ticketType, priority);

            // Load entities
            User user = userRepository.findByEmployeeId(employeeId)
                .orElseThrow(() -> new ResourceNotFoundException(
                    "Không tìm thấy nhân viên: " + employeeId));

            Vehicle vehicle = null;
            if (vehicleId != null) {
                vehicle = vehicleRepository.findByVehicleId(vehicleId)
                    .orElseThrow(() -> new ResourceNotFoundException(
                        "Không tìm thấy phương tiện: " + vehicleId));
            }

            // Generate ticket ID
            String ticketId = generateTicketId();

            // Build comprehensive event
            SupportTicketEvent event = eventBuilder.buildTicketCreatedEvent(
                ticketId,
                user,
                vehicle,
                ticketType,
                priority,
                title,
                description,
                stationId,
                stationName,
                stationAddress,
                batteryPackId,
                incidentTime,
                attachments,
                correlationId
            );

            // Publish với error handling
            eventPublisher.publishSupportTicketCreated(event)
                .whenComplete((result, ex) -> {
                    if (ex != null) {
                        log.error("✗ Critical: Failed to publish support ticket event", ex);
                        // In production: Alert DevOps, save to DLQ, trigger retry
                    } else {
                        log.info("✓ Support ticket event published successfully: " +
                            "ticketId={}, partition={}, offset={}", 
                            ticketId,
                            result.getRecordMetadata().partition(),
                            result.getRecordMetadata().offset());
                    }
                });

            log.info("Detailed support ticket created: ticketId={}, correlationId={}", 
                ticketId, correlationId);

            return ticketId;

        } finally {
            MDC.remove("correlationId");
        }
    }

    /**
     * Example 3: Tạo urgent/critical ticket với immediate notification
     */
    public String createUrgentSupportTicket(
            String employeeId,
            String vehicleId,
            SupportTicketType ticketType,
            String title,
            String description
    ) {

        String correlationId = UUID.randomUUID().toString();
        MDC.put("correlationId", correlationId);

        try {
            log.warn("⚠️ URGENT: Creating critical support ticket: employeeId={}, type={}", 
                employeeId, ticketType);

            User user = userRepository.findByEmployeeId(employeeId)
                .orElseThrow(() -> new ResourceNotFoundException(
                    "User not found: " + employeeId));

            Vehicle vehicle = vehicleRepository.findByVehicleId(vehicleId)
                .orElse(null);

            String ticketId = generateTicketId();

            // Build event với CRITICAL priority
            SupportTicketEvent event = eventBuilder.buildTicketCreatedEvent(
                ticketId,
                user,
                vehicle,
                ticketType,
                TicketPriority.CRITICAL, // CRITICAL priority
                title,
                description,
                null, null, null, null,
                Instant.now(),
                null,
                correlationId
            );

            // Publish và đợi kết quả (synchronous cho urgent case)
            try {
                eventPublisher.publishSupportTicketCreated(event)
                    .get(); // Block và đợi publish thành công

                log.warn("✓ URGENT ticket published successfully: ticketId={}", ticketId);

                // TODO: Gửi SMS/Push notification ngay lập tức cho staff

            } catch (Exception ex) {
                log.error("✗ CRITICAL: Failed to publish urgent ticket: ticketId={}", 
                    ticketId, ex);
                // TODO: Fallback mechanism - gọi API trực tiếp, alert DevOps
                throw new RuntimeException("Failed to create urgent support ticket", ex);
            }

            return ticketId;

        } finally {
            MDC.remove("correlationId");
        }
    }

    /**
     * Generate unique ticket ID
     * Format: TKT-{timestamp}-{random}
     */
    private String generateTicketId() {
        return String.format("TKT-%d-%s", 
            Instant.now().toEpochMilli(),
            UUID.randomUUID().toString().substring(0, 8));
    }
}

package com.boilerplate.auth.service;

import com.boilerplate.auth.entity.User;
import com.boilerplate.auth.entity.Vehicle;
import com.boilerplate.auth.enums.SupportTicketType;
import com.boilerplate.auth.enums.TicketPriority;
import com.boilerplate.auth.model.event.SupportTicketEvent;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Component;

import java.time.Instant;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.UUID;

/**
 * Helper class để build SupportTicketEvent từ domain entities
 * 
 * Tách biệt logic build event khỏi business logic
 */
@Component
@RequiredArgsConstructor
@Slf4j
public class SupportTicketEventBuilder {

    @Value("${spring.application.name:auth-service}")
    private String serviceName;

    /**
     * Build SupportTicketEvent từ các thông tin ticket
     */
    public SupportTicketEvent buildTicketCreatedEvent(
            String ticketId,
            User user,
            Vehicle vehicle,
            SupportTicketType ticketType,
            TicketPriority priority,
            String title,
            String description,
            Long stationId,
            String stationName,
            String stationAddress,
            String batteryPackId,
            Instant incidentTime,
            List<String> attachments,
            String correlationId
    ) {

        Instant now = Instant.now();
        String eventId = UUID.randomUUID().toString();

        // Build business data
        SupportTicketEvent.SupportTicketData data = SupportTicketEvent.SupportTicketData.builder()
                .ticketId(ticketId)
                .employeeId(user.getEmployeeId())
                .employeeName(user.getFullName())
                .contactEmail(user.getEmail())
                .contactPhone(user.getPhone())
                .vehicleId(vehicle != null ? vehicle.getVehicleId() : null)
                .vin(vehicle != null ? vehicle.getVin() : null)
                .licensePlate(vehicle != null ? vehicle.getLicensePlate() : null)
                .vehicleModel(vehicle != null ? vehicle.getModel() : null)
                .batteryPackId(batteryPackId)
                .stationId(stationId)
                .stationName(stationName)
                .stationAddress(stationAddress)
                .ticketType(ticketType)
                .priority(priority)
                .title(title)
                .description(description)
                .attachments(attachments)
                .incidentTime(incidentTime)
                .createdAt(now)
                .build();

        // Build metadata
        Map<String, Object> metadata = new HashMap<>();
        metadata.put("service", serviceName);
        metadata.put("version", "1.0");
        metadata.put("environment", System.getProperty("spring.profiles.active", "unknown"));

        // Build event
        SupportTicketEvent event = SupportTicketEvent.builder()
                .eventId(eventId)
                .source(String.format("/%s/support-tickets", serviceName))
                .specVersion("1.0")
                .type("com.evbss.support.ticket.created")
                .eventTime(now)
                .correlationId(correlationId != null ? correlationId : UUID.randomUUID().toString())
                .dataContentType("application/json")
                .data(data)
                .metadata(metadata)
                .build();

        log.debug("Built SupportTicketEvent: eventId={}, ticketId={}", eventId, ticketId);

        return event;
    }

    /**
     * Build event với minimal info (simplified version)
     */
    public SupportTicketEvent buildSimpleTicketCreatedEvent(
            String ticketId,
            String employeeId,
            String employeeName,
            String contactEmail,
            SupportTicketType ticketType,
            String title,
            String description
    ) {

        Instant now = Instant.now();
        String eventId = UUID.randomUUID().toString();

        SupportTicketEvent.SupportTicketData data = SupportTicketEvent.SupportTicketData.builder()
                .ticketId(ticketId)
                .employeeId(employeeId)
                .employeeName(employeeName)
                .contactEmail(contactEmail)
                .ticketType(ticketType)
                .priority(TicketPriority.MEDIUM)
                .title(title)
                .description(description)
                .createdAt(now)
                .build();

        return SupportTicketEvent.builder()
                .eventId(eventId)
                .source(String.format("/%s/support-tickets", serviceName))
                .specVersion("1.0")
                .type("com.evbss.support.ticket.created")
                .eventTime(now)
                .correlationId(UUID.randomUUID().toString())
                .dataContentType("application/json")
                .data(data)
                .metadata(Map.of("service", serviceName, "version", "1.0"))
                .build();
    }
}
